import React, { useState } from 'react';
import axios from 'axios';

function LoginForm({ setIsAuthenticated }) {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
    });

    const [token, setToken] = useState(''); // Added state to store the token

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Send a POST request to the authentication endpoint
            const response = await axios.post(
                'http://127.0.0.1:8000/api-token-auth/',
                {
                    username: formData.username,
                    password: formData.password,
                }
            );

            // Extract the token from the response data and store it in state
            const { token } = response.data;
            setToken(token);

            // Store the token in local storage
            localStorage.setItem('token', token);

            // Update the authentication status
            setIsAuthenticated(true);

            // Handle success, e.g., redirect to a new page
            console.log('Login Successful. Token:', token);
        } catch (error) {
            if (error.response) {
                // The request was made, but the server responded with an error
                console.error('Server Error:', error.response.data);
            } else if (error.request) {
                // The request was made, but no response was received
                console.error('No Response:', error.request);
            } else {
                // Something else went wrong
                console.error('Error:', error.message);
            }

            // Handle error, e.g., display an error message to the user
            // You can set a state variable to display an error message on the form
            // Example: setError('Login failed. Please check your credentials.');
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit">Login</button>
            </form>

            {token && <p>Token Received: {token}</p>} {/* Display the token if it exists */}
        </div>
    );
}

export default LoginForm;
